import React, { useState } from "react";
import "../css/Portfolio.css";

import translateme from "../assets/translateme.png";
import rickandmorty1 from "../assets/rickMorty1.png";
import tonester from "../assets/tonester.png";
import uber from "../assets/uber.png";
import portfoliov1 from "../assets/portfoliov1.png";
import portfoliov2 from "../assets/portfoliov2.png";
import marsrover from "../assets/marsrover.png";
import portfoliov3 from "../assets/portfoliov3.png";

import { Container, Row, Col, Modal } from "react-bootstrap";

const Portfolio = () => {
  const pages = [
                {
                img: translateme,
                name:"translateme"
                },
                {
                  img: tonester,
                  name:"tonester"
                  },
                  {
                    img: uber,
                    name:"uber"
                    },
                    {
                      img: portfoliov1,
                      name:"portfoliov1"
                      },
                      {
                        img: rickandmorty1,
                        name:"rickandmorty1"
                        },
                        {
                          img: portfoliov2,
                          name:"portfoliov2"
                          },
                          {
                            img: marsrover,
                            name:"marsrover"
                            },
                            {
                              img: portfoliov3,
                              name:"portfoliov3"
                              }, 
  ];
  const projects = [{
      title: "translateme",
      description:
        "Translate me uses a machine learning technology to classify and translate images into different languages. Once the image is captured, users can flip the image to reveal the desired translated word for the image.",
      technologies: [
        "Javascript",
        "React",
        "RubyonRails",
        "IBMWatson",
        "Cloudinary",
        "SemanticUI",
      ],
      images: [
        "https://raw.githubusercontent.com/MarioR9/TranslateMeFrontend/master/ReadmeImages/main.png",
        "https://github.com/MarioR9/TranslateMeFrontend/raw/master/ReadmeImages/categories.png",
        "https://github.com/MarioR9/TranslateMeFrontend/raw/master/ReadmeImages/categoryCards.png",
        "https://github.com/MarioR9/TranslateMeFrontend/raw/master/ReadmeImages/selectLanguage.png",
      ],
      social: [
        {
        icon: '<i className="fab fa-github portfolio__fa-lg"></i>', 
        link: 'https://github.com/MarioR9/TranslateMeFrontend'
        },
        {
        icon: '<i className="fab fa-youtube portfolio__fa-lg" ></i>', 
        link: 'https://www.youtube.com/watch?v=M7LLMVlS6hg&feature=emb_logo'
        },
    ]
    },
  ];

  const handledSelectedProject = (e) =>{
    debugger
    return projects.find(project => project.title == e.target.id)
  }
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);

  return (
    <div id="portfolio">
      <Container
        data-aos="fade-up"
        data-aos-delay="30"
        data-aos-easing="ease-in-out"
        data-aos-offset="200"
        data-aos-duration="1200"
      >
        <Container>
          <Modal
            size="xl"
            centered
            enforceFocus
            show={show}
            onHide={handleClose}
            className="portfolio__modalContainer"
          >
            <Modal.Header closeButton></Modal.Header>
            <Modal.Body centered scrollable>
              <Container>
                <Row>
                  <Col
                  >
                    <h2
                    data-aos="fade-down"
                    data-aos-delay="40"
                    data-aos-easing="ease-in-out"
                    data-aos-offset="100"
                    data-aos-duration="1200"
                    >{handledSelectedProject.Title}</h2>
                    <p
                    data-aos="fade-down"
                    data-aos-delay="40"
                    data-aos-easing="ease-in-out"
                    data-aos-offset="100"
                    data-aos-duration="1200"
                    >{handledSelectedProject.description}</p>

                  </Col>
                </Row>
              </Container>
            </Modal.Body>
            <Modal.Footer></Modal.Footer>
          </Modal>
        </Container>

        <div className="portfolio">
          <div className="portfolio__header">
            <h3>Recent Projects</h3>
          </div>
          <div className="portfolio__grid">
            {pages.map((page) => (
              <ul
                key={page}
                data-aos="zoom-in"
                data-aos-delay="30"
                data-aos-easing="ease-in-out"
                data-aos-offset="200"
                data-aos-duration="1200"
              >
                <img
                  data-id={page.toString()}
                  onClick={handledSelectedProject}
                  className="portfolio__img"
                  src={page}
                  alt={page}
                />
              </ul>
            ))}
          </div>
        </div>
      </Container>
    </div>
  );
};
export default Portfolio;
