import React from 'react'
import '../css/About.css'
import aboutImg from '../assets/about.jpg'

import { Container,Row,Col } from 'react-bootstrap';


export const About = () => {
    const about= 'I am a Full-stack web developer with a passion for problem-solving. With experience in Ruby on Rails, JavaScript, and React I discovered web development while taking computer science classes and I fell in love with it. I bring strong skills in working with people, communication in English and Spanish as well as managing people. I spend my free time playing guitar as well as building small projects and learning about new technologies.'
    return (
        <div className="about">
           <Container>
            <Row>
            <Col>
            <img className='about__img' src={aboutImg}/>
            </Col>
            <Col>
            <h2>About Me</h2>
            {about}
            </Col>
            </Row>

            </Container>
        </div>
    )
}
export default About;
